package com.fas.fahiru.vo;

public class Step {
	
	private int no, cookNo, cookOrder;
	private String cookStepImg, cookStepTxt;
	
	public Step() {
		// TODO Auto-generated constructor stub
	}

	public int getNo() {
		return no;
	}

	public void setNo(int no) {
		this.no = no;
	}

	public int getCookNo() {
		return cookNo;
	}

	public void setCookNo(int cookNo) {
		this.cookNo = cookNo;
	}

	public int getCookOrder() {
		return cookOrder;
	}

	public void setCookOrder(int cookOrder) {
		this.cookOrder = cookOrder;
	}

	public String getCookStepImg() {
		return cookStepImg;
	}

	public void setCookStepImg(String cookStepImg) {
		this.cookStepImg = cookStepImg;
	}

	public String getCookStepTxt() {
		return cookStepTxt;
	}

	public void setCookStepTxt(String cookStepTxt) {
		this.cookStepTxt = cookStepTxt;
	}
	
	

}
